function findMax(numbers) {
  // Handle empty array
  if (numbers.length === 0) {
    return null;
  }

  // Start with first number
  let max = numbers[0];

  // Check each number
  for (let i = 1; i < numbers.length; i++) {
    let current = numbers[i];
    if (current > max) {
      max = current;
    }
  }

  // Return maximum
  return max;
}

function countWords(sentence) {
  // Handle empty string
  if (sentence.length === 0) {
    return 0;
  }

  // Trim whitespace
  let trimmed = sentence.trim();

  // Check empty after trim
  if (trimmed.length === 0) {
    return 0;
  }

  // Split by spaces
  let words = trimmed.split(' ');

  // Filter empty strings
  let filtered = words.filter(w => w.length > 0);

  // Return count
  return filtered.length;
}

function isPalindrome(text) {
  // Convert to lowercase
  let lower = text.toLowerCase();

  // Remove spaces
  let clean = lower.replace(/ /g, '');

  // Get length
  let length = clean.length;

  // Check from both ends
  for (let i = 0; i < length / 2; i++) {
    let left = clean[i];
    let right = clean[length - 1 - i];

    if (left !== right) {
      return false;
    }
  }

  return true;
}

function factorial(n) {
  // Handle 0 and 1
  if (n === 0) {
    return 1;
  }
  if (n === 1) {
    return 1;
  }

  // Initialize result
  let result = 1;

  // Multiply from 2 to n
  for (let i = 2; i <= n; i++) {
    result = result * i;
  }

  // Return result
  return result;
}

function fibonacci(n) {
  // Handle base cases
  if (n === 0) {
    return 0;
  }
  if (n === 1) {
    return 1;
  }

  // Initialize first two numbers
  let prev = 0;
  let curr = 1;

  // Calculate up to n
  for (let i = 2; i <= n; i++) {
    let next = prev + curr;
    prev = curr;
    curr = next;
  }

  return curr;
}

function calculateAverage(numbers) {
  // Check empty array
  if (numbers.length === 0) {
    return 0;
  }

  // Sum all numbers
  let sum = 0;
  for (let i = 0; i < numbers.length; i++) {
    sum = sum + numbers[i];
  }

  // Calculate average
  let count = numbers.length;
  let average = sum / count;

  // Return average
  return average;
}

function countCharacter(text, char) {
  // Initialize counter
  let count = 0;

  // Convert to lowercase
  let lowerText = text.toLowerCase();
  let lowerChar = char.toLowerCase();

  // Loop through text
  for (let i = 0; i < lowerText.length; i++) {
    let current = lowerText[i];

    if (current === lowerChar) {
      count = count + 1;
    }
  }

  return count;
}

function toTitleCase(text) {
  // Split into words
  let words = text.split(' ');

  // Process each word
  let result = [];
  for (let i = 0; i < words.length; i++) {
    let word = words[i];

    if (word.length > 0) {
      let first = word[0].toUpperCase();
      let rest = word.slice(1).toLowerCase();
      result.push(first + rest);
    }
  }

  // Join words
  return result.join(' ');
}

function filterEvens(numbers) {
  // Initialize result array
  let evens = [];

  // Loop through numbers
  for (let i = 0; i < numbers.length; i++) {
    let num = numbers[i];

    // Check if even
    let remainder = num % 2;
    if (remainder === 0) {
      evens.push(num);
    }
  }

  // Return even numbers
  return evens;
}
